import React, { useState } from "react";
import { UilSearch, UilLocationPoint } from "@iconscout/react-unicons";
import { toast } from "react-toastify";

const Inputs = ({ setQuery, units, setUnits }) => {
  const [city, setCity] = useState("");

  const handleSearchClick = (event) => {
    event.preventDefault();
    if (city !== "") setQuery({ q: city });
  };

  const handleUnitsChange = (e) => {
    const selectedUnit = e.target.name;
    if (units !== selectedUnit) setUnits(selectedUnit);
  };

  const handleLocationClick = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        toast.success(`Location fetched Successfully.`);
        let lat = position.coords.latitude;
        let lon = position.coords.longitude;

        setQuery({
          lat,
          lon,
        });
      });
    }
  };

  return (
    <div className="flex-row justify-center my-6 md:flex">
      <form
        onSubmit={handleSearchClick}
        className="flex flex-row md:w-3/4 items-center border-r-black justify-center space-x-4"
      >
        <input
          value={city}
          onChange={(e) => {
            setCity(e.target.value);
          }}
          type="text"
          className="md:text-xl p-2 font-light w-full shadow-xl capitalize focus:outline-none"
          placeholder="Search..."
        />
        <UilSearch
          type="submit"
          size={25}
          className="text-white cursor-pointer transition ease-out hover:scale-125"
        />
        <UilLocationPoint
          onClick={handleLocationClick}
          size={25}
          className="text-white cursor-pointer transition ease-out hover:scale-125"
        />
      </form>
      <div className="md:w-1/4 flex justify-center items-center">
        <div className="flex md:pt-0 bg-black p-5 rounded-2xl justify-center items-center pt-2">
        <button
          onClick={handleUnitsChange}
          name="metric"
          className="text-sm text-white font-light transition ease-out hover:scale-125"
        >
          &deg;C
        </button>
        <p className="text-white text-sm mx-2">|</p>
          <button
            onClick={handleUnitsChange}
            name="imperial"
            className="text-sm text-white font-light transition ease-out hover:scale-125"
          >
            &deg;F
          </button>
          </div>
      </div>
    </div>
  );
};

export default Inputs;
