import React from 'react'
import { formatToLocalTime } from '../services/services'

const TimeLocation = ({weather: {dt, timezone, name, country}}) => {
  return (
    <div>
    <div className='flex items-center justify-center my-6 p-2 border-r-8 bg-gray-800'>

        <p className='text-white text-xl text-center font-extralight'>
            {formatToLocalTime(dt, timezone)}
        </p>

    </div>
    <div className='flex items-center justify-center my-3'>
        <p className='text-white text-3xl font-medium'>
            {`${name}, ${country}`}
        </p>
    </div>
    </div>
  )
}

export default TimeLocation
