import React, { useState } from 'react'
import { UilSearch, UilLocationPoint } from '@iconscout/react-unicons'
import { toast } from 'react-toastify';

const Inputs = ({setQuery, units, setUnits}) => {

  const [city, setCity] = useState("");

  const handleSearchClick = () => {
    if(city !== '') setQuery({q: city})
  }

  const handleUnitsChange = (e) => {
    const selectedUnit = e.target.name;
    if(units !== selectedUnit) setUnits(selectedUnit);
  }

  const handleLocationClick = () => {
    if(navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        toast.success(`Location fetched Successfully.`)
        let lat = position.coords.latitude
        let lon = position.coords.longitude

        setQuery({
          lat,
          lon
        })
      })
    }
  }

  return (
    <div className='flex-row justify-center my-6 md:flex'>
        <form className='flex flex-row md:w-3/4 items-center border-r-black justify-center space-x-4'>
            <input 
            value={city}
            onChange={(e)=>{setCity(e.target.value)}}
            type='text' 
            className='md:text-xl p-2 font-light w-full shadow-xl capitalize focus:outline-none' 
            placeholder='Search...'/>
            <UilSearch type="submit" onClick={handleSearchClick} size={25} className='text-white cursor-pointer transition ease-out hover:scale-125'/>
            <UilLocationPoint onClick={handleLocationClick} size={25} className='text-white cursor-pointer transition ease-out hover:scale-125'/>
        </form>
        <div className='flex pt-3 md:pt-0 flex-row justify-center items-center md:w-1/4'>
            <button onClick={handleUnitsChange} name='metric' className='text-xl text-white font-light transition ease-out hover:scale-125'>&deg;C</button>
            <p className='text-white text-xl mx-2'>|</p>
            <label class="flex items-center space-x-2">
  <input type="checkbox" class="form-checkbox h-5 w-5 text-indigo-600" id="toggle" />
  <button onClick={handleUnitsChange} name='imperial' className='text-xl text-white font-light transition ease-out hover:scale-125'>&deg;F</button>
</label>

            </div>

    </div>
  )
}

export default Inputs
