import { DateTime } from "luxon";

const API_KEY = "7810862358638f2199f060783ceacbf6";
const BASE_URL = "https://api.openweathermap.org/data/2.5";

const getWeatherData = (infoType, searchParams) => {
  const url = new URL(BASE_URL + "/" + infoType);
  url.search = new URLSearchParams({ ...searchParams, appid: API_KEY });

  return fetch(url).then((res) => res.json());
};

const weatherFormat = (data) => {
  const {
    coord: { lat, lon },
    main: { temp, feels_like, temp_min, temp_max, pressure, humidity },
    wind: { speed },
    dt,
    name,
    cod,
    sys: { country, sunrise, sunset },
  } = data;

  const { main: details, icon } = data.weather[0];

  return {
    lat,
    lon,
    temp,
    feels_like,
    temp_min,
    temp_max,
    pressure,
    humidity,
    speed,
    dt,
    name,
    cod,
    country,
    sunrise,
    sunset,
    details,
    icon,
  };
};

const forecastFormat = async (data) => {
  let { timezone, daily } = data;

  daily = daily.slice(1, 4).map(d => {
    return {
        title: formatToLocalTime(d.dt, timezone, 'ccc'),
        temp: d.temp.day,
        icon: d.weather[0].icon
    }
  });

  return { timezone, daily }
};

const getFormattedWeatherData = async (searchParams) => {
  const formattedData = await getWeatherData("weather", searchParams).then(
    weatherFormat
  );

  const { lat, lon } = formattedData;

  const formattedForcast = await getWeatherData("onecall", {
    lat,
    lon,
    exclude: "current, minutely, alerts",
    units: searchParams.units,
  }).then(forecastFormat);

  return {...formattedData, ...formattedForcast};
};

const formatToLocalTime = (
  secs,
  zone,
  format = "cccc, dd, LLL, yyy' | Local time: 'hh:mm a"
) => DateTime.fromSeconds(secs).setZone(zone).toFormat(format);

export default getFormattedWeatherData;
