import React, { useState } from 'react'
import { UilSearch, UilLocationPoint } from '@iconscout/react-unicons'

const Inputs = ({setQuery, units, setUnits}) => {

  const [city, setCity] = useState("");

  const handleSearchClick = () => {
    if(city !== '') setQuery({q: city})
  }

  return (
    <div className='flex flex-row justify-center my-6'>
        <div className='flex flex-row w-3/4 items-center justify-center space-x-4'>
            <input 
            value={city}
            onChange={(e)=>{setCity(e.target.value)}}
            type='text' 
            className='text-xl p-2 font-light w-full shadow-xl capitalize focus:outline-none' 
            placeholder='Search...'/>
            <UilSearch size={25} className='text-white cursor-pointer transition ease-out hover:scale-125'/>
            <UilLocationPoint size={25} className='text-white cursor-pointer transition ease-out hover:scale-125'/>
        </div>
        <div className='flex flex-row justify-center items-center w-1/4'>
            <button name='metric' className='text-xl text-white font-light transition ease-out hover:scale-125'>&deg;C</button>
            <p className='text-white text-xl mx-2'>|</p>
            <button name='imperial' className='text-xl text-white font-light transition ease-out hover:scale-125'>&deg;F</button>
        </div>

    </div>
  )
}

export default Inputs
