import React from 'react'

const TopButtons = ({setQuery}) => {

    const cities = [
        {
            id: 1,
            title: 'Jammu'
        },
        {
            id: 2,
            title: 'Assam'
        },
        {
            id: 3,
            title: 'Kerala'
        },
        {
            id: 4,
            title: 'Gujarat'
        },
        {
            id: 5,
            title: 'Jharkhand'
        },
    ]

  return (
    <div className='hidden md:flex items-center sm:justify-evenly my-6 '>
        {cities.map((city) => (
            <button onClick={() => {
                setQuery({ q: city.title })
            }} key={city.id} className='text-white shadow-black text-sm font-medium md:textlg'>{city.title}</button>
        ))}
    </div>
  )
}

export default TopButtons
